import UIKit

class WeatherViewController: UIViewController, WeatherViewProtocol {
	var presenter: WeatherPresenterProtocol?
    
    @IBOutlet weak var weatherTableView: UITableView!
    @IBOutlet weak var tatleLabel: UILabel!
    
	override func viewDidLoad() {
        super.viewDidLoad()
        tatleLabel.text = presenter?.getTitle()
        presenter?.loadData()
    }

    func update() {
        DispatchQueue.main.async {
            self.weatherTableView.reloadData()
            let sectionIndex = IndexSet(integer: 0)
            self.weatherTableView.reloadSections(sectionIndex, with: .none)
        }
    }
    
    func showMessage(title: String, text: String, action repeatHandler: @escaping ()->()) {
        let alertAction = UIAlertAction(title: "OK", style: .default) { (action) in
            repeatHandler()
        }
        let controller = UIAlertController(title: title, message: text, preferredStyle: .alert)
        controller.addAction(alertAction)
        
        DispatchQueue.main.async {
            self.present(controller, animated: true, completion: nil)
        }
    }
    
    func showError(text: String) {
        DispatchQueue.main.async {
            // self.indicator.stopAnimating()
            self.showMessage(title: "ERROR", text: text) { () in
                
            }
        }
    }
}

extension WeatherViewController:  UITableViewDataSource {
    func numberOfSections(in tableView: UITableView) -> Int {
        return 1
    }
    
    func tableView(_ tableView: UITableView, numberOfRowsInSection section: Int) -> Int {
        return presenter?.count() ?? 0
    }
    
    func tableView(_ tableView: UITableView, cellForRowAt indexPath: IndexPath) -> UITableViewCell {
        let cell = tableView.dequeueReusableCell(withIdentifier:"WeatherTableViewCell", for: indexPath)

        if let cell = cell as? WeatherTableViewCellProtocol {
            let text = presenter?.getTextWeatherInfo(index: indexPath.row) ?? ""
            cell.configCell(text: text)
        }
        return cell
    }
}

extension WeatherViewController: UITableViewDelegate {
    func tableView(_ tableView: UITableView, didSelectRowAt indexPath: IndexPath) {
    }
}
